mapper.select:
SELECT ${sqlMap.column.toSql()}
FROM ${sqlMap.table.toSql()}
LEFT JOIN kube_vm_log AS vm
ON  vm.apply_id = a.id

	 a.start_date >= DATE_ADD(NOW(), INTERVAL 5 MINUTE)
	 AND a.`status` = 0
	 AND  vm.apply_id is null

ORDER BY ${sqlMap.order.toSql()}

SELECT ${sqlMap.column.toSql()}
FROM ${sqlMap.table.toSql()}

	a.end_date <= NOW()
	AND a.`status` = 0

ORDER BY ${sqlMap.order.toSql()}
